import React from "react";
import { Link } from "react-router-dom";

const AchievementsTeaser = () => {
  const images = [
    "/achievements/award1.jpg",
    "/achievements/award2.jpg",
    "/achievements/award3.jpg"
  ];

  return (
    <section className="py-16 bg-white">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
        <h2 className="text-3xl sm:text-4xl font-bold text-slate-900 mb-4">
          Achievements üèÜ
        </h2>
        <p className="text-lg text-slate-600 mb-8">
          A few highlights from my journey ‚Äî academic milestones, certifications,
          and accomplishments that shaped my career path.
        </p>

        {/* Preview images */}
        <div className="grid grid-cols-2 sm:grid-cols-3 gap-6 mb-6">
          {images.map((img, idx) => (
            <div
              key={idx}
              className="rounded-lg overflow-hidden shadow-md bg-slate-50"
            >
              <img
                src={img}
                alt={`Achievement ${idx + 1}`}
                className="w-full h-36 object-cover"   // üîë smaller height
              />
              <p className="text-slate-700 text-sm font-medium py-2">
                Achievement {idx + 1}
              </p>
            </div>
          ))}
        </div>

        {/* View more link */}
        <Link
          to="/achievements"
          className="inline-flex items-center text-emerald-600 font-medium hover:underline"
        >
          View more details ‚Üí
        </Link>
      </div>
    </section>
  );
};

export default AchievementsTeaser;
