import React, { useState, useEffect } from "react";

const FunFacts = () => {
  // Add your hobby images in /public/funfacts/ folder
  const images = [
    { src: "/funfacts/sketch1.jpg", label: "Sketching" },
    { src: "/funfacts/sketch2.jpg", label: "Sketching" },
    { src: "/funfacts/poetry1.jpg", label: "Poetry" },
    { src: "/funfacts/cooking1.jpg", label: "Cooking" },
    { src: "/funfacts/cooking2.jpg", label: "Cooking" },
    { src: "/funfacts/travel1.jpg", label: "Travel & Exploring" },
    { src: "/funfacts/friends1.jpg", label: "Making Friends" },
    { src: "/funfacts/movies1.jpg", label: "Movies" },
  ];

  const [current, setCurrent] = useState(0);

  // Auto-play every 4 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrent((prev) => (prev + 1) % images.length);
    }, 4000);
    return () => clearInterval(interval);
  }, [images.length]);

  const goPrev = () => {
    setCurrent((prev) => (prev === 0 ? images.length - 1 : prev - 1));
  };

  const goNext = () => {
    setCurrent((prev) => (prev + 1) % images.length);
  };

  return (
    <section className="py-20 bg-gradient-to-br from-slate-50 to-emerald-50 min-h-screen">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-12">
          <h2 className="text-4xl sm:text-5xl font-bold text-slate-900 mb-4">
            Fun <span className="text-emerald-600">Facts</span>
          </h2>
          <p className="text-lg text-slate-600 max-w-3xl mx-auto">
            A little more about me outside work — I enjoy{" "}
            <span className="font-semibold">sketching</span>,{" "}
            <span className="font-semibold">writing poetry</span> (in English & Hindi),{" "}
            <span className="font-semibold">cooking</span>,{" "}
            <span className="font-semibold">dancing</span>,{" "}
            <span className="font-semibold">traveling</span>,{" "}
            <span className="font-semibold">watching movies</span>, and{" "}
            <span className="font-semibold">making new friends</span>.
          </p>
        </div>

        {/* Carousel */}
        <div className="relative w-full max-w-3xl mx-auto">
          <div className="relative h-[350px] sm:h-[450px] md:h-[550px] rounded-xl shadow-lg overflow-hidden flex justify-center items-center bg-white">
            <img
              src={images[current].src}
              alt={images[current].label}
              className="max-h-[350px] sm:max-h-[450px] md:max-h-[550px] w-auto object-contain transition-all duration-700"
            />
            <div className="absolute bottom-4 bg-slate-900/70 text-white px-4 py-2 rounded-md text-sm font-medium">
              {images[current].label}
            </div>

            {/* Prev Button */}
            <button
              onClick={goPrev}
              className="absolute top-1/2 left-4 transform -translate-y-1/2 bg-slate-900/50 text-white px-3 py-2 rounded-full hover:bg-slate-900 transition"
            >
              ◀
            </button>

            {/* Next Button */}
            <button
              onClick={goNext}
              className="absolute top-1/2 right-4 transform -translate-y-1/2 bg-slate-900/50 text-white px-3 py-2 rounded-full hover:bg-slate-900 transition"
            >
              ▶
            </button>
          </div>

          {/* Dots */}
          <div className="flex justify-center mt-4 space-x-2">
            {images.map((_, idx) => (
              <button
                key={idx}
                onClick={() => setCurrent(idx)}
                className={`w-3 h-3 rounded-full ${idx === current ? "bg-emerald-600" : "bg-gray-300"
                  }`}
              ></button>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default FunFacts;
