import React from "react";
import Slider from "react-slick";
import { Link } from "react-router-dom";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const FunFacts = () => {
  const images = [
    "Cooking.jpg",
    "Poetry1.png", "Poetry2.png", "Poetry3.jpg", "Poetry4.png", "Poetry5.jpg", "Poetry6.png", "Poetry7.png",
    "Sketch1.jpg", "Sketch2.jpg", "Sketch3.jpg", "Sketch4.jpg", "Sketch5.jpg"
  ];

  // Carousel settings
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3, // how many images visible at once
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2500,
    responsive: [
      { breakpoint: 1024, settings: { slidesToShow: 2 } },
      { breakpoint: 640, settings: { slidesToShow: 1 } }
    ]
  };

  return (
    <section className="min-h-screen bg-slate-100 text-slate-900 py-16 px-6">
      <h1 className="text-4xl font-bold text-center mb-6">Fun Facts Gallery ðŸŽ‰</h1>
      <p className="text-lg text-center mb-12 max-w-3xl mx-auto text-slate-600">
        A closer look at my creative side â€” from poems and sketches to cooking experiments.
        These hobbies keep me inspired and balanced outside of work.
      </p>

      {/* Carousel */}
      <div className="max-w-6xl mx-auto">
        <Slider {...settings}>
          {images.map((file, index) => (
            <div key={index} className="px-3">
              <img
                src={`/funfacts/${file}`}
                alt={`Fun Fact ${index + 1}`}
                className="w-full h-80 object-cover rounded-xl shadow-lg hover:scale-105 transition"
              />
            </div>
          ))}
        </Slider>
      </div>

      {/* Back Button */}
      <div className="text-center mt-12">
        <Link
          to="/"
          className="px-6 py-3 bg-emerald-600 hover:bg-emerald-700 rounded-xl text-white font-semibold transition"
        >
          â¬… Back to Home
        </Link>
      </div>
    </section>
  );
};

export default FunFacts;
