import React from "react";
import { Link } from "react-router-dom";
import portfolioData from "../data/mockData";

const Insights = () => {
  const { blogPosts = [] } = portfolioData; // using blogPosts if you want same UI
  // Or use insights: const { insights = [] } = portfolioData;

  return (
    <section className="p-8 bg-white min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-center">Latest Insights</h1>
      <p className="text-center text-slate-600 mb-12">
        Sharing knowledge and discoveries from the fascinating world of bioinformatics and computational biology
      </p>

      <div className="grid gap-6 md:grid-cols-3">
        {blogPosts.map((post) => (
          <div
            key={post.id}
            className="bg-white p-6 rounded-2xl shadow hover:shadow-lg transition"
          >
            <h2 className="text-xl font-semibold mb-2">{post.title}</h2>
            <p className="text-sm text-gray-500 mb-4">{post.readTime}</p>
            <p className="text-gray-700 mb-4">{post.excerpt}</p>

            {/* ✅ Link to detail page */}
            <Link
              to={`/insights/${post.id}`}
              className="text-emerald-600 font-medium hover:underline"
            >
              Read Article →
            </Link>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Insights;
