import React from "react";
import { Link } from "react-router-dom";
import portfolioData from "../data/mockData";

const Insights = () => {
  const { insights = [] } = portfolioData;

  return (
    <section className="p-8 bg-white min-h-screen">
      <h1 className="text-3xl font-bold mb-6">Latest Insights</h1>

      <div className="grid gap-6 md:grid-cols-2">
        {insights.map((item) => (
          <Link
            to={`/insights/${item.id}`}
            key={item.id}
            className="p-6 bg-gray-100 rounded-2xl shadow hover:shadow-lg transition block"
          >
            <h2 className="text-xl font-semibold">{item.title}</h2>
            <p className="text-sm text-gray-500 mt-2">{item.readTime}</p>
            <p className="mt-3 text-gray-700">{item.summary}</p>
          </Link>
        ))}
      </div>
    </section>
  );
};

export default Insights;
